import { StatusBar } from 'expo-status-bar';
import React,{useState} from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';

export default function App() {

    const [cep,setCep] =useState('');
    const [info,setInfo] =useState(null);


    async function buscar(){

      if(cep == ""){
        alert("Digite um CEP");
        return;
      }

      try{
      const cepHttp = await fetch(`https://free.currencyconverterapi.com/api/v5/convert?q=USD_BRL&compact=ultra&apiKey=5d7ac80eb63372db62ca`);
      const cepJson = await cepHttp.json();
      setInfo(cepJson);
      }catch(error){
        alert("Digite um CEP valido");
        setCep("");
        setInfo("");
      }

    }
    function limpar(){

      setCep('');

    }

  return (
    <View style={styles.container}>


      <Text  style={styles.texto}> Projeto API Dos Correios </Text> 

  <TextInput  placeholder = "Digite o seu CEP" value ={cep} onChangeText = {(texto) => setCep(texto)} />

    <TouchableOpacity style={styles.btn} onPress = {buscar}>

   <Text style={{fontWeight:'bold', color:'white'}}> Buscar </Text> 
   </TouchableOpacity>
   <TouchableOpacity style={styles.btn} onPress ={limpar}>
   <Text style={{fontWeight:'bold', color:'white'}}> Limpar </Text> 
   </TouchableOpacity>

    {info &&
 <View style={styles.resultado}>
 <Text  style={styles.texto1}>Rua: {info.USD_BRL} </Text> 
 <Text  style={styles.texto1}>CEP: {info.cep} </Text> 
 <Text  style={styles.texto1}>Bairro: {info.bairro} </Text> 
 <Text  style={styles.texto1}>Cidade: {info.localidade} </Text> 
 <Text  style={styles.texto1}>Estado: {info.uf} </Text> 
 </View>
}
    </View>

  );  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#bff',
    alignItems: 'center',
    marginTop: 25

  },
  texto:{

    fontSize: 20,
    alignItems: 'center'
  },
  texto1:{
    width: '100%',
    height: '30%',
    alignItems: 'center',



  },
  btn:{
    backgroundColor: '#d90000',
    width: '80%',
    alignItems: 'center',
   marginTop: 5,

    fontSize: 20


  },
  resultado:{

    width: '80%',
    alignItems: 'center',
    marginTop: 25

  }
});
