import React from 'react';
import { StyleSheet, Text, View, TextInput, TouchableOpacity } from 'react-native';

export default function App() {
  return (
    <View style={styles.container}>


      <Text  style={styles.texto}> Projeto API Dos Correios </Text> 

  <TextInput  placeholder = "Digite o seu CEP" />

<TouchableOpacity style={styles.btn}>

   <Text style={{fontWeight:'bold', color:'white'}}> Buscar </Text> 
   </TouchableOpacity>
   <TouchableOpacity style={styles.btn}>
   <Text style={{fontWeight:'bold', color:'white'}}> Limpar </Text> 
 </TouchableOpacity>

    </View>
  );  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#bff',
    alignItems: 'center',
    marginTop: 25

  },
  texto:{

    fontSize: 20,
    alignItems: 'center'
  },
  btn:{
    backgroundColor: '#d09',
    width: '100%',
    alignItems: 'center',
   marginTop: 5,
